    public String getRequestUrl(String cityId) throws IOException, InterruptedException {
        int destination = getDestinationId(cityId);
        int numberOfPeople = searchParameters.getNumberOfPeople();
        String checkIn = searchParameters.getCheckIn();
        String checkOut = searchParameters.getCheckOut();
        String optionalParameters;
        String requestUrl;
        if (searchParameters.isMaxPriceSet()) {
            requestUrl = "https://hotels4.p.rapidapi.com/properties/list?adults1="+ numberOfPeople + "&pageNumber=1&destinationId=" + destination + "&pageSize=25&checkOut="+ checkOut +"&checkIn="+ checkIn + "&priceMax=" + searchParameters.getMaxPrice() + "&sortOrder=PRICE&locale=en_US&currency=USD";

        } else {
            requestUrl = "https://hotels4.p.rapidapi.com/properties/list?adults1="+ numberOfPeople + "&pageNumber=1&destinationId=" + destination + "&pageSize=25&checkOut="+ checkOut +"&checkIn="+ checkIn + "&sortOrder=PRICE&locale=en_US&currency=USD";

        }
        requestUrl += "&priceMin=" + searchParameters.getMinPrice();
        return requestUrl;
    }
